import StatusSwitch from '@britania-crm/web-components/StatusSwitch'
import Tooltip from '@britania-crm/web-components/Tooltip'

import useStyles from './styles'

const MainData = ({
  isEdit,
  isDisabled,
  formRef,
  setCpf,
  cpfAlreadyExists,
  isView
}) => {
  const t = useT()
  const classes = useStyles()
