  const snackbar = useSnackbar()

  const [line, setLine] = useState('')
  const [family, setFamily] = useState([])
  const [listByLineOfFamily, setListByLineOfFamily] = useState([])
  const [matrixCode, setMatrixCode] = useState('')
  const [disabledButton, setDisabledButton] = useState(false)
  const mockVoltage = useMemo(() => [{ id: '110', name: '110' }, { id: '220', name: '220' }], [])

  const familiesParams = useMemo(
    () => {
      if (line) {
        return {
          clientTotvsCode: matrixCode,
          lines: line
        }
      }
    },
    [line, matrixCode]
  )

  const {
    data: linesFromApi,
    loading: linesFromApiLoading
  } = useCrmApi(
    matrixCode ? [linesCrmRoutes.getAll, matrixCode] : null,
    { params: { clientTotvsCode: matrixCode } },
    {
      onErrorRetry (error, key, config, revalidate, { retryCount }) {
        if (error.response.status === 500 && retryCount < 5 && !isView) {
          createDialog({
            id: 'new-request-modal',
            Component: ConfirmModal,
            props: {
              onConfirm () {
                revalidate({ retryCount })
              },
              text: t('search error line')
            }
          })
        } else {
          dispatch(AppActions.addAlert({ type: 'error', message: t('maximum number of attempts reached') }))
        }
      },
      revalidateOnFocus: false
    })

  const {
    data: familiesFromApi,
    loading: familiesFromApiLoading
  } = useCrmApi(
    (matrixCode && line) ? [linesCrmRoutes.getFamilies, familiesParams] : null,
    { params: familiesParams },
    {
      onErrorRetry (error, key, config, revalidate, { retryCount }) {
        if (error.response.status === 500 && retryCount < 5 && !isView) {
          createDialog({
            id: 'new-request-family-modal',
            Component: ConfirmModal,
            props: {
              onConfirm () {
                revalidate({ retryCount })
              },
              text: t('search error family')
            }
          })
        } else {
          dispatch(AppActions.addAlert({ type: 'error', message: t('maximum number of attempts reached') }))
        }
      },
      revalidateOnFocus: false
    })

  const columns = useMemo(() => [
    {
      title: t('line', { howMany: 1 }),
      field: 'lineDescription',
      width: '50%'
    },
    {
      title: t('family', { howMany: 1 }),
      field: 'family',
      width: '50%',
      render (row) {
        if (row?.family) {
          const listFamilyString = map(row.family, ({ familyDescription }) => familyDescription)
          const shortListFamilyString = listFamilyString.slice(0, 4).join(', ')
          return (
            <div>
              <Tooltip title={ listFamilyString.join(', ') } arrow>
                {
                  listFamilyString.length > 4
                    ? shortListFamilyString.concat('...')
                    : shortListFamilyString
                }
              </Tooltip>
            </div>
          )
        }
        return ''
      }
    }
  ], [t])

  const OPTIONS_LINE = useMemo(
    () => filter(
      linesFromApi,
      (item) => !find(listByLineOfFamily, ({ lineCode }) => lineCode === item.lineCode)
    ),
    [linesFromApi, listByLineOfFamily]
  )

  const doAddItemTable = useCallback(
    () => {
      const arrayFamily = map(family, (item) => find(familiesFromApi, ({ familyCode }) => familyCode === item))
      const arrayLine = find(linesFromApi, ({ lineCode }) => lineCode === line)

      formRef.current.setFieldValue('linesFamilies', (old) => [
        ...old,
        {
          family: [...arrayFamily],
          ...arrayLine
        }
      ])
      setFamily([])
      setLine('')
    },
    [familiesFromApi, family, formRef, line, linesFromApi]
  )

  const handleLineFamily = useCallback(
    (value) => {
      setListByLineOfFamily(value)
      if (isEmpty(value)) {
        formRef.current.setFieldValue('responsible', {})
        formRef.current.setFieldValue('regionalManager', {})
      }
    },
    [formRef]
  )

  const clientParams = useMemo(
    () => ({
      page: 1,
      pageSize: 10,
      clientRegistrationType: 'REGISTER'
    }),
    []
  )

  const linesAndFamilies = useMemo(
    () => {
      const linesFamilies = []
      if (!isEmpty(family) && line) {
        map(family, (item) => {
          linesFamilies.push(`${ line },${ item }`)
        })
      }
      return linesFamilies
    }
    , [family, line])

  const regionalParams = useMemo(
    () => ({
      page: 1,
      pageSize: 10,
      linesAndFamilies
    }),
    [linesAndFamilies]
  )

  const respresentativeParams = useMemo(
    () => ({
      page: 1,
      pageSize: 10,
      linesAndFamilies
    }),
    [linesAndFamilies]
  )

  const { loading: regionalFromApiLoading } = useCrmApi(
    matrixCode && !isEmpty(linesAndFamilies) ? [customerCrmRoutes.getRegional.replace(':clientCode', matrixCode, linesAndFamilies), regionalParams] : null,
    { params: regionalParams },
    {
      onSuccess (data) {
        const regional = formRef.current.getFieldValue('regionalManager')

        if (first(data)?.approverCode !== regional.approverCode && !isEmpty(regional)) {
          setDisabledButton(true)
          setFamily([])
          setLine('')
          snackbar.error(t('many managers found'))
        } else {
          setDisabledButton(false)
        }

        if (isEmpty(regional)) {
          formRef.current.setFieldValue('regionalManager', first(data))
        }
      },
      onErrorRetry (error) {
        if (error.response.status === 500) {
          snackbar.error(getErrorMessage(error))
        }
      },
      revalidateOnFocus: false
    }
  )

  const { loading: representativeFromApiLoading } = useCrmApi(
    matrixCode && !isEmpty(listByLineOfFamily) ? [customerCrmRoutes.getResponsible.replace(':clientCode', matrixCode, listByLineOfFamily), respresentativeParams] : null,
    { params: respresentativeParams },
    {
      revalidateOnFocus: false,
      onSuccess (data) {
        const responsible = formRef.current.getFieldValue('responsible')
        if (isEmpty(responsible)) {
          formRef.current.setFieldValue('responsible', first(data))
        }
      },
      onErrorRetry (error) {
        if (error.response.status === 500) {
          snackbar.error(getErrorMessage(error))
        }
      }
    }
  )

  const isDisabledButton = useMemo(
    () =>
      (isEmpty(family) && isEmpty(line)) ||
      isDisabled ||
      disabledButton ||
      regionalFromApiLoading
    , [disabledButton, family, isDisabled, line, regionalFromApiLoading])

  const setNameMatrix = useCallback(
    (value) => {
      setLine('')
      formRef.current.setFieldValue('clientTotvsDescription', value)
      setMatrixCode(value.parentCompanyCode)
    },
    [formRef]
  )

  const setCodeMatrix = useCallback(
    (value) => {
      setLine('')
      formRef.current.setFieldValue('clientTotvsCode', value)
      setMatrixCode(value.parentCompanyCode)
    },
    [formRef]
  )

  const handleDeleteItem = useCallback((event, row) =>
    formRef.current.setFieldValue('linesFamilies', filter(listByLineOfFamily, (option, index) => index !== row.tableData.id))
  , [formRef, listByLineOfFamily])

  return (
    <CustomAccordion header={ t('main data') }>
      <Grid container spacing={ 1 } className={ classes.containerMain }>
        <Grid item sm={ 12 } md={ 4 } >
          <InputAutocomplete
            url={ customerCrmRoutes.getInfoCustomer }
            params={ clientParams }
            valueKey="parentCompanyCode"
            paramName="parentCompanyCode"
            name="clientTotvsCode"
            label={ t('matrix code', { abbreviation: false }) }
            disabled={ isEdit || isDisabled }
            onValueChange={ setNameMatrix }
          />
        </Grid>
        <Grid item sm={ 12 } md={ 4 } >
          <InputAutocomplete
            url={ customerCrmRoutes.getInfoCustomer }
            params={ clientParams }
            valueKey="parentCompanyName"
            paramName="parentCompany"
            name="clientTotvsDescription"
            label={ t('matrix', { howMany: 1 }) }
            disabled={ isEdit || isDisabled }
            onValueChange={ setCodeMatrix }
          />
        </Grid>
        <Grid item sm={ 12 } md={ 4 } >
          <InputCpfCnpj
            name="cpf"
            label="CPF"
            mode="cpf"
            disabled={ isDisabled }
            onValueChange={ setCpf }
            helperText={ cpfAlreadyExists ? 'CPF jÃ¡ cadastrado em outra Matriz' : '' }
          />
        </Grid>
        <Grid item sm={ 12 }>
          <InputText
            name="name"
            label={ t('name', { howMany: 1 }) }
            disabled={ isDisabled }
            inputProps={ { maxLength: 200 } }
          />
        </Grid>
        <Grid item sm={ 12 }>
          <InputText
            name="category"
            label={ t('category', { howMany: 1 }) }
            disabled={ isDisabled }
            inputProps={ { maxLength: 200 } }
          />
        </Grid>
        <Grid item sm={ 12 } md={ 5 } >
          <InputSelect
            detached
            valueKey="lineDescription"
            idKey="lineCode"
            value={ line }
            loading={ linesFromApiLoading }
            onChange={ ({ target }) => {
              setLine(target.value)
              setFamily([])
            } }
            name="line"
            label={ t('line', { howMany: 1 }) }
            id="select-line"
            required
            options={ OPTIONS_LINE }
            disabled={ isDisabled || !linesFromApi }
          />
        </Grid>
        <Grid item sm={ 12 } md={ 5 }>
          <InputSelect
            detached
            valueKey="familyDescription"
            idKey="familyCode"
            disabled={ isDisabled || isEmpty(familiesFromApi) }
            value={ family }
            onChange={ ({ target }) => setFamily(target.value) }
            name="family"
            label={ t('family', { howMany: 1 }) }
            id="select-family"
            loading={ familiesFromApiLoading }
            required
            options={ familiesFromApi }
            multiple
          />
        </Grid>
        <Grid item sm={ 12 } md={ 2 } className={ classes.containerAddButton }>
          <I18n
            id="add-button"
            as={ Button }
            size="small"
            startIcon={ <Add /> }
            variant="contained"
            onClick={ doAddItemTable }
            className={ classes.addButton }
            disabled={ isDisabledButton }
          >
            datagrid body add tooltip
          </I18n>
        </Grid>
        <Grid item sm={ 12 } className={ classes.containerTable }>
          <div className={ classes.table }>
            <DataTable
              data={ listByLineOfFamily }
              columns={ columns }
              loading={ false }
              onDeleteClick={ !isDisabled && handleDeleteItem }
              options={ { search: false } }
            />
            <InputHidden
              name="linesFamilies"
              onValueChange={ handleLineFamily }
              showError
            />
          </div>
        </Grid>
        <Grid item sm={ 12 } md={ 6 } >
          <InputAutocomplete
            valueKey="approverDescription"
            name="responsible"
            label={ t('responsible', { howMany: 1 }) }
            disabled={ true }
            loading={ representativeFromApiLoading }
          />
        </Grid>
        <Grid item sm={ 12 } md={ 6 } >
          <InputAutocomplete
            loading={ regionalFromApiLoading }
            valueKey="approverDescription"
            name="regionalManager"
            label={ t('regional manager') }
            disabled={ true }
          />
        </Grid>
        <Grid item sm={ 12 } md={ 2 } >
          <InputSelect
            name="voltage"
            label={ t('voltage', { howMany: 1 }) }
            options={ mockVoltage }
            disabled={ isDisabled }
          />
        </Grid>
        <Grid item sm={ 12 } md={ 4 } >
          <InputText
            name="role"
            label={ t('office', { howMany: 1 }) }
            disabled={ isDisabled }
            inputProps={ { maxLength: 50 } }
          />
        </Grid>
        <Grid item sm={ 12 } md={ 2 } >
          <InputDayMonth
            label={ t('birthday', { howMany: 1 }) }
            type="tel"
            mode="dd/mm"
            name="birthday"
            disabled={ isDisabled }
          />
        </Grid>
        <Grid item sm={ 12 } md={ 4 } >
          <InputText
            name="email"
            label={ t('email') }
            disabled={ isDisabled }
            inputProps={ { maxLength: 40 } }
          />
        </Grid>
        <Grid item sm={ 12 } md={ 4 } >
          <InputPhone
            name="telephone"
            label={ t('phone', { howMany: 1 }) }
            disabled={ isDisabled }
          />
        </Grid>
        {isEdit && (
          <Grid item sm={ 12 } md={ 2 } className={ classes.status }>
            <StatusSwitch
              name="active"
              disabled={ isDisabled }
            />
          </Grid>)}
      </Grid>
    </CustomAccordion>
  )
}

MainData.propTypes = {
  isEdit: PropTypes.bool,
  isDisabled: PropTypes.bool.isRequired,
  formRef: PropTypes.any.isRequired,
  setCpf: PropTypes.func.isRequired,
  cpfAlreadyExists: PropTypes.bool,
  isView: PropTypes.bool
}

MainData.defaultProps = {
  isEdit: false,
  cpfAlreadyExists: false,
  isView: false
}

export default MainData
